import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-saludo',
  templateUrl: './saludo.component.html',
  styleUrls: ['./saludo.component.scss']
})
export class SaludoComponent implements OnInit {

//aqui deben declararce las variables. 

@Input() nombre: string = 'Anonimo' //del padre al hijo
@Output()  //sirve para gestionar eventos de abajo hacia arriba,  hijo al padre, el componente superiro debe siempre tener la logica

  constructor() { }

  ngOnInit(): void {
    //instrucciones previas a la renderizacion del componente
  }

  //aqui se ve en que consiste los metodos, y esto es lo que desencadena los eventos. 
  //en este casovamos a hacer un metodo para gestionar una alerta.
  alertaSaludo(): void {
    alert (`hola, ${this.nombre}, alerta despachada desde un click de boton`)
  }

}
<h1>!Hola {{ nombre }}¡</h1>
<h3 id="usuario" [innerText]="nombre">
    <!--no indicamos nada en las estiquetas sino que el valor viene definido en la propiedad.
    podemos acceder a todas las propiedad de elementos html y modificarlas con un valor que se encuentre en typescript
    el innertext que es aquello que se establece dentro de las etiquetas-->
</h3>
<button id="emit-alerta" (click)="enviarMensajeAlPadre()">Mostrar alerta</button> <!--para emitir la alerta hay que hacer la logica del boton-->
